std::mt19937 rng(std::random_device{}());
constexpr venti operator""_vt(uloli x){return venti(x);}
constexpr bool ying=false,yang=true;
constexpr int N=2001;
int n,m,l,q;
loli ans[N];
bool v[N],w[N][N];
std::vector<tiii>a[N];
std::vector<tiiii>b;
bsc s;
loli d[N][N];
void add(int x,int y,int k){
	for(int i=x;i<=n;i+=i&-i)
		for(int j=y;j<=m;j+=j&-j)
			d[i][j]+=k;
}
loli ask(int x,int y){
	loli k=0;
	for(int i=x;i;i-=i&-i)
		for(int j=y;j;j-=j&-j)
			k+=d[i][j];
	return k;
}
signed main(){
//	freopen(".in","r",stdin);
//	freopen(".out","w",stdout);
	std::ios::sync_with_stdio(false);cin.tie(nullptr);
#define cin fio
#define cout fio
	cin>>n>>m>>l;
	for(int i=1,z;i<=l;i++){
		cin>>z;a[i].resize(z);
		for(auto&[x,y,c]:a[i])cin>>x>>y>>c;
	}
	std::fill(v+1,v+1+l,yang);
	cin>>q;
	for(int i=1;i<=q;i++){
		cin>>s;
		if(s.front()=='A'){
			memcpy(w[siz(b)]+1,v+1,sizeof(bool)*l);
			auto&[x1,y1,x2,y2]=b.emplace_back();
			cin>>x1>>y1>>x2>>y2;
		}else{
			int p;cin>>p;
			v[p]=!v[p];
		}
	}
	for(int i=1;i<=l;i++){
		for(auto[x,y,c]:a[i])add(x,y,c);
		for(int j=0;j<siz(b);j++)
			if(auto[x1,y1,x2,y2]=b[j];w[j][i])
				ans[j]+=ask(x2,y2)-ask(x1-1,y2)-ask(x2,y1-1)+ask(x1-1,y1-1);
		for(auto[x,y,c]:a[i])add(x,y,-c);
	}
	for(int j=0;j<siz(b);j++)cout<<ans[j]<<'\n';
	return 0;
}